// BottomAppBarWithNavigation.js
import React, { useState, useEffect } from 'react';
import styled from '@mui/system/styled';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import MenuIcon from '@mui/icons-material/Menu';
import EditIcon from '@mui/icons-material/Edit';
import Divider from '@mui/material/Divider';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import Box from '@mui/material/Box';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Inbox from '@mui/icons-material/Inbox';
import DnsRoundedIcon from '@mui/icons-material/DnsRounded';
import Send from '@mui/icons-material/Send';
import Delete from '@mui/icons-material/Delete';
import Info from '@mui/icons-material/Info';
import Drafts from '@mui/icons-material/Drafts';
import Folder from '@mui/icons-material/Folder';
import Button from '@mui/material/Button';
import Avatar from '@mui/material/Avatar';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import { useNavigate } from 'react-router-dom';

// Importar constantes de estilo
import { circularIconButtonStyles, homeIconButtonStyles, itemStyles, itemCategoryStyles, StyledAppBar, CenteredBox, CircularIconButton, HomeIconButton } from '../Estilos/Navigator';

// Función para renderizar el menú de navegación
function renderNavigationMenu(categories, navigate) {
  return categories.map(({ id, children }) => (
    <Box key={id} sx={{ bgcolor: ' #344955 ' }}>
      <ListItem key={id} sx={{ py: 2, px: 3 }}>
        <ListItemText sx={{ color: '#fff' }}>{id}</ListItemText>
      </ListItem>

      {children.map(({ id: childId, icon, active, onClick }) => (
        <ListItem key={childId} disablePadding>
          <ListItemButton selected={active} sx={itemStyles} onClick={onClick}>
            <ListItemIcon>{icon}</ListItemIcon>
            <ListItemText>{childId}</ListItemText>
          </ListItemButton>
        </ListItem>
      ))}

      <Divider sx={{ mt: 2 }} />
    </Box>
  ));
}

// Función para renderizar el diálogo de perfil de usuario
function renderUserProfileDialog(openDialog, handleCloseDialog, nombre, correo_electronico, foto_perfil_url) {
  return (
    <Dialog open={openDialog} onClose={handleCloseDialog}>
      <DialogTitle sx={{ backgroundColor: '#333', color: '#FFF' }}>{nombre}</DialogTitle>
      <DialogContent>
        <Box display="flex" alignItems="center" justifyContent="center" flexDirection="column">
          <Avatar src={foto_perfil_url} />
          <List>
            <ListItem>
              <ListItemText
                primary="Correo Electrónico"
                secondary={correo_electronico}
                primaryTypographyProps={{ variant: 'subtitle1', fontWeight: 'bold', color: '#333' }}
              />
            </ListItem>
          </List>
        </Box>
      </DialogContent>
    </Dialog>
  );
}

export default function BottomAppBarWithNavigation({ PaperProps, variant, open, onClose, miFuncionEnPaperbase, data }) {
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [openDialog, setOpenDialog] = useState(false);

  const [funcionEnPaperbase, setFuncionEnPaperbase] = useState(miFuncionEnPaperbase);
  const [correo_electronico, nombre, foto_perfil_url] = (data && data.length > 0) ? data[0] : {};

  const handleInboxClick = () => {
    console.log('Clic en Bandeja de entrada');
    navigate('/guardados');
  };

  const handleBasuraClick = () => {
    console.log('Clic en Basura');
    navigate('/delete', { state: { data } });
  };

  const handleMenuToggle = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleProfileClick = () => {
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  useEffect(() => {
    // Manejar cambios en miFuncionEnPaperbase si es necesario
    setFuncionEnPaperbase(() => miFuncionEnPaperbase);
  }, [miFuncionEnPaperbase]);

  return (
    <>
      <StyledAppBar>
        <Toolbar>
          <CenteredBox>
            <IconButton size="large" edge="start" color="#344955 " aria-label="open drawer" onClick={handleMenuToggle}>
              <MenuIcon />
            </IconButton>
            <HomeIconButton size="large" color="#fff" aria-label="edit">
              <EditIcon />
            </HomeIconButton>
            <IconButton size="large" edge="end" color="#fff" aria-label="account">
              <AccountCircleIcon />
            </IconButton>
          </CenteredBox>
        </Toolbar>
      </StyledAppBar>

      <Drawer variant="temporary" open={isMenuOpen} onClose={handleMenuToggle}>
        <List disablePadding sx={{ bgcolor: ' #344955 ', height: '100%' }}>
          <ListItem key="user-info" sx={{ ...itemStyles, ...itemCategoryStyles, fontSize: 22, color: '#fff', justifyContent: 'flex-end' }}>
            <ListItem key="user-info">
              <ListItemIcon>
                <Avatar alt={nombre} src={foto_perfil_url} onClick={handleProfileClick} />
              </ListItemIcon>
            </ListItem>

            {renderUserProfileDialog(openDialog, handleCloseDialog, nombre, correo_electronico, foto_perfil_url)}
          </ListItem>

          <ListItem key="compose" sx={{ ...itemStyles, ...itemCategoryStyles }}>
            <Button
              variant="outlined"
              sx={{
                borderRadius: '25px',
                color: '#344955 ',
                bgcolor: '#FFA500',
                typography: {
                  fontFamily: 'Libre Franklin',
                  fontWeight: 'bold',
                  fontSize: '15px',
                  letterSpacing: '0.15px',
                },
              }}
            >
              <ListItemIcon>
                <EditIcon />
              </ListItemIcon>
              COMPOSE
            </Button>
          </ListItem>

          {renderNavigationMenu([
            {
              children: [
                { id: 'Bandeja de entrada', icon: <Inbox />, onClick: handleInboxClick },
                { id: 'Base de datos', icon: <DnsRoundedIcon /> },
                { id: 'Enviado', icon: <Send /> },
                { id: 'Basura', icon: <Delete />, onClick: handleBasuraClick },
                { id: 'Spam', icon: <Info /> },
                { id: 'Borrador', icon: <Drafts /> },
              ],
            },
            {
              id: 'Carpetas',
              children: [
                { id: 'Ingreso', icon: <Folder /> },
                { id: 'Vacaciones', icon: <Folder /> },
                { id: 'Impuesto', icon: <Folder /> },
              ],
            },
          ], navigate)}
        </List>
      </Drawer>

      <div style={{ marginLeft: isMenuOpen ? '240px' : '0', padding: '16px', marginTop: '64px', transition: 'margin-left 0.3s ease-in-out' }}></div>
    </>
  );
}
