import React, { useState } from 'react';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/Inbox';
import SendIcon from '@mui/icons-material/Send';
import DraftsIcon from '@mui/icons-material/Drafts';

const categories = [
{
id: 'Mail',
icon: <Avatar alt="Gmail" src="URL_DEL_ICONO" />, // Reemplaza "URL_DEL_ICONO" con la URL de tu icono de Gmail
children: [
{ id: 'Inbox', icon: <InboxIcon />, active: true },
{ id: 'Outgoing', icon: <SendIcon /> },
{ id: 'Drafts', icon: <DraftsIcon /> },
],
},
];

const App = () => {
const [open, setOpen] = useState(false);

const handleOpen = () => {
setOpen(true);
};

const handleClose = () => {
setOpen(false);
};

return (
<div>
<button onClick={handleOpen}>Abrir caj√≥n</button>
{open && (
<Drawer
variant="permanent"
anchor="left"
width="240"
open={open}
onClose={handleClose}
>
<List>
{categories.map(({ id, icon, children }) => (
<div key={id} style={{ width: '100%' }}>
<div style={{ backgroundColor: '#1E88E5', color: '#fff' }}>
{icon}
</div>
<List>
{children.map(({ id: childId, icon, active }) => (
<ListItem key={childId}>
<ListItemIcon>{icon}</ListItemIcon>
<ListItemText>{childId}</ListItemText>
</ListItem>
))}
</List>
</div>
))}
</List>
</Drawer>
)}
</div>
);
};

export default App;
