import * as React from 'react';
import BottomAppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import CssBaseline from '@mui/material/CssBaseline';
import Navigator from './Navigator';

export default function App() {
  const [drawerOpen, setDrawerOpen] = React.useState(false);

  const toggleDrawer = () => {
    setDrawerOpen(!drawerOpen);
  };

  return (
    <div style={{ display: 'flex' }}>
      <CssBaseline />
      {/* Contenido principal */}
      <main style={{ flexGrow: 1, padding: '64px' }}>
        {/* Contenido de la aplicación */}
        {/* ... (tu contenido principal aquí) */}
      </main>

      {/* Barra de aplicación inferior directamente desde Material-UI */}
      <BottomAppBar position="fixed" sx={{ top: 'auto', bottom: 0 }}>
        <Toolbar>
          <IconButton
            edge="start"
            color="inherit"
            aria-label="open drawer"
            onClick={toggleDrawer}
          >
            <MenuIcon />
          </IconButton>
          {/* Agrega otros elementos de la barra inferior según sea necesario */}
        </Toolbar>
      </BottomAppBar>

      {/* Barra de navegación lateral */}
      <Navigator open={drawerOpen} onClose={toggleDrawer} />
    </div>
  );
}
