import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { Container, Typography, TextField, Grid, Paper } from '@mui/material';

const DataBase = () => {
    const [data, setData] = useState(null);
    const [filteredData, setFilteredData] = useState(null);
    const [selectedDate, setSelectedDate] = useState(null);
    const location = useLocation();
    const { info } = location.state || {};

    useEffect(() => {
        fetchDataFromBackend();
    }, []);

    useEffect(() => {
        filterDataByDate();
    }, [data, selectedDate]);

    const fetchDataFromBackend = async () => {
        try {
            const response = await fetch('http://localhost:80/guardados');
            if (response.ok) {
                const jsonData = await response.json();
                setData(jsonData);
            } else {
                console.error('Error al obtener datos desde el backend:', response.status);
            }
        } catch (error) {
            console.error('Error de red:', error);
        }
    };

    const filterDataByDate = () => {
        if (!selectedDate) {
            setFilteredData(data);
        } else {
            const filtered = data.filter(item => {
                // Suponiendo que cada objeto en `data` tiene una propiedad `fecha` que contiene la fecha en formato 'YYYY-MM-DD'
                return item.fecha === selectedDate;
            });
            setFilteredData(filtered);
        }
    };

    const handleDateChange = (event) => {
        setSelectedDate(event.target.value);
    };

    return (
        <Container sx={{ pt: 4 }}>
            <Typography variant="h4" gutterBottom>
                Base de Datos
            </Typography>
            <Grid container spacing={2} alignItems="center">
                <Grid item xs={12} sm={6}>
                    <TextField
                        id="dateFilter"
                        label="Filtrar por fecha"
                        type="date"
                        value={selectedDate}
                        onChange={handleDateChange}
                        InputLabelProps={{
                            shrink: true,
                        }}
                        fullWidth
                    />
                </Grid>
            </Grid>
            <Grid container spacing={2} sx={{ mt: 3 }}>
                {filteredData && filteredData.map((item, index) => (
                    <Grid key={index} item xs={12} sm={6} md={4} lg={3}>
                        <Paper elevation={3} sx={{ p: 2, borderRadius: 2 }}>
                            {Object.keys(item).map((key, index) => (
                                <div key={index} style={{ marginBottom: '10px' }}>
                                    <Typography variant="h6" sx={{ color: 'text.secondary', mb: 1 }}>
                                        {key}:
                                    </Typography>
                                    <Typography variant="body1">
                                        {item[key]}
                                    </Typography>
                                </div>
                            ))}
                        </Paper>
                    </Grid>
                ))}
            </Grid>
        </Container>
    );
};

export default DataBase;
