import React from 'react';
import { Container, Typography, Card, CardContent, makeStyles } from '@mui/material';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    backgroundColor: theme.palette.primary.main, // Color primario
  },
  card: {
    maxWidth: 400,
    padding: theme.spacing(2),
    backgroundColor: '#fff', // Color de fondo de las tarjetas
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: theme.spacing(2),
  },
}));

function Home() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Container>
        <Card className={classes.card} elevation={3}>
          <CardContent>
            <Typography className={classes.title} color="textPrimary">
              #Acerca de Reply
            </Typography>
            <Typography variant="body1" color="textSecondary">
              #Reply es una aplicación que ayuda a personas y grupos a comunicarse. Está diseñado para brindar claridad, legibilidad, intuición y facilidad de uso.
            </Typography>
            {/* Agrega más contenido aquí */}
          </CardContent>
        </Card>
      </Container>
    </div>
  );
}

export default Home;
