import React from 'react';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Avatar from '@mui/material/Avatar';
import InboxIcon from '@mui/icons-material/Inbox';
import SendIcon from '@mui/icons-material/Send';
import DraftsIcon from '@mui/icons-material/Drafts';

const categories = [
  {
    id: 'Mail',
    icon: <Avatar alt="Gmail" src="URL_DEL_ICONO" />, // Reemplaza "URL_DEL_ICONO" con la URL de tu icono de Gmail
    children: [
      { id: 'Inbox', icon: <InboxIcon />, active: true },
      { id: 'Outgoing', icon: <SendIcon /> },
      { id: 'Drafts', icon: <DraftsIcon /> },
    ],
  },
];

const drawerStyle = {
  width: 240,
  flexShrink: 0,
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
};

const drawerPaperStyle = {
  width: 240,
  backgroundColor: '#1976D2',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  paddingTop: '20px', // Ajusta el espaciado superior según tus necesidades
};

const itemCategory = {
  py: 1.5,
  px: 3,
  bgcolor: '#1E88E5',
  color: '#fff',
  textAlign: 'center',
};

const userEmailStyle = {
  color: '#fff',
  marginTop: 'auto', // Esto empujará el correo electrónico hacia abajo
};

export default function Navigator(props) {
  const { ...other } = props;

  return (
    <Drawer
      variant="permanent"
      {...other}
      sx={{ ...drawerStyle, '& .MuiDrawer-paper': drawerPaperStyle }}
    >
      <List disablePadding>
        {categories.map(({ id, icon, children }) => (
          <div key={id} style={{ width: '100%' }}>
            <div style={{ ...itemCategory }}>
              {icon}
            </div>
            <List>
              {children.map(({ id: childId, icon, active }) => (
                <ListItem disablePadding key={childId}>
                  <ListItemButton selected={active} sx={{ py: 2, px: 3 }}>
                    <ListItemIcon>{icon}</ListItemIcon>
                    <ListItemText>{childId}</ListItemText>
                  </ListItemButton>
                </ListItem>
              ))}
            </List>
          </div>
        ))}
      </List>
      <div style={userEmailStyle}>
        <p>ejemplo@gmail.com</p>
      </div>
    </Drawer>
  );
}
