import React, { useEffect, useState } from 'react';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import CardActions from '@mui/material/CardActions';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import Dialog from '@mui/material/Dialog';
import DialogContent from '@mui/material/DialogContent';
import IconButton from '@mui/material/IconButton';
import FullscreenIcon from '@mui/icons-material/Fullscreen';

function Content() {
  const [rawData, setRawData] = useState('');
  const [mediaItems, setMediaItems] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedMediaItem, setSelectedMediaItem] = useState(null);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const dataParam = params.get('data');

    if (dataParam) {
      setRawData(dataParam);
    }
  }, []);

  useEffect(() => {
    try {
      const data = JSON.parse(rawData);
      if (Array.isArray(data)) {
        setMediaItems(data);
      }
    } catch (error) {
      console.error('Error al analizar datos en bruto:', error);
    }
  }, [rawData]);

  const handleMediaItemClick = (mediaItem) => {
    setSelectedMediaItem(mediaItem);
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setSelectedMediaItem(null);
    setOpenDialog(false);
  };

  return (
    <div>
      {mediaItems.length > 0 ? (
        <div>
          <Grid container spacing={2}>
            {mediaItems.map((mediaItem, index) => (
              <Grid item xs={12} sm={6} md={4} key={index}>
                <Card
                  sx={{
                    height: '100%',
                    display: 'flex',
                    flexDirection: 'column',
                  }}
                >
                  {mediaItem.isVideo ? (
                    <CardMedia
                      component="video"
                      sx={{
                        pt: '75%',
                        backgroundColor: 'blue',
                        cursor: 'pointer',
                      }}
                      controls
                      src={mediaItem.url}
                      onClick={() => handleMediaItemClick(mediaItem)}
                    />
                  ) : (
                    <CardMedia
                      component="div"
                      sx={{
                        pt: '75%',
                        backgroundColor: 'blue',
                        cursor: 'pointer',
                      }}
                      image={mediaItem.url}
                      onClick={() => handleMediaItemClick(mediaItem)}
                    />
                  )}

                  <CardActions sx={{ backgroundColor: '#eaeff1' }}>
                    <Button size="small">Edit</Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>

          {/* Diálogo para mostrar la imagen o video en pantalla completa */}
          <Dialog open={openDialog} onClose={handleCloseDialog} fullWidth maxWidth="md">
            <DialogContent>
              {selectedMediaItem ? (
                selectedMediaItem.isVideo ? (
                  <video controls width="100%" height="auto">
                    <source src={selectedMediaItem.url} type="video/mp4" />
                    Tu navegador no admite la etiqueta de video.
                  </video>
                ) : (
                  <img src={selectedMediaItem.url} alt="FullScreen" style={{ width: '100%', height: 'auto' }} />
                )
              ) : null}
            </DialogContent>
          </Dialog>
        </div>
      ) : (
        <p>No hay imágenes o videos para mostrar.</p>
      )}
    </div>
  );
}

export default Content;
