import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';

function Delete() {
  const location = useLocation();

  useEffect(() => {
    const sendDataToBackend = async () => {
      try {
        if (location.state && location.state.data) {
          const data = location.state.data;
          const response = await fetch(`http://localhost:80/eliminar?data=${encodeURIComponent(JSON.stringify(data))}`);
          if (response.ok) {
            const responseData = await response.json();
            console.log(responseData); // Esto imprimirá la respuesta desde tu backend
          } else {
            console.error('Error al enviar datos al backend:', response.statusText);
          }
        }
      } catch (error) {
        console.error('Error al enviar datos al backend:', error);
      }
    }

    sendDataToBackend();
  }, [location]);

  
}

export default Delete;
