import React, { useState } from 'react';
import { Routes, Route, Link, useLocation } from 'react-router-dom';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';

import CompBase from "./Paginas/CompBase";
import Ho from "./Paginas/Ho";
import Default from "./Paginas/Default";
import Dashboard from "./Paginas/Dashboard";

function App() {
  const [anchorEl, setAnchorEl] = useState(null);
  const location = useLocation(); // Utilizamos useLocation para obtener la ubicación actual

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  // Determinamos si debemos mostrar la interfaz de inicio de sesión en función de la ubicación actual
  const showLoginInterface = location.pathname !== '/photos';

  return (
    <div>
      {showLoginInterface && ( // Mostrar la interfaz de inicio de sesión solo si showLoginInterface es true
        <AppBar position="static">
          <Toolbar>
            <IconButton
              edge="start"
              color="inherit"
              aria-label="menu"
              onClick={handleClick}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant="h6" style={{ flex: 1 }}>
              Mi Aplicación
            </Typography>
            <Button color="inherit" component={Link} to="/">
              Inicio
            </Button>
            <Button color="inherit" component={Link} to="/photos">
              Fotos
            </Button>
            <Button color="inherit" component={Link} to="/dashboard">
              Panel de Control
            </Button>
            <Button color="inherit" onClick={handleClick}>
              <AccountCircleIcon sx={{ marginRight: 1 }} />
              Iniciar Sesión
            </Button>
            <Menu
              anchorEl={anchorEl}
              open={Boolean(anchorEl)}
              onClose={handleClose}
            >
              <MenuItem onClick={handleClose}>
                <AccountCircleIcon sx={{ marginRight: 1 }} />
                Mi Perfil
              </MenuItem>
              <MenuItem onClick={handleClose}>
                <AccountCircleIcon sx={{ marginRight: 1 }} />
                Configuración
              </MenuItem>
              <MenuItem onClick={handleClose}>
                <AccountCircleIcon sx={{ marginRight: 1 }} />
                Cerrar Sesión
              </MenuItem>
            </Menu>
          </Toolbar>
        </AppBar>
      )}
      <Routes>
        <Route path="/" element={<Ho />} />
        <Route path="/photos" element={<CompBase />} />
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="*" element={<Default />} />
      </Routes>
    </div>
  );
}

export default App;
