import React, { useEffect, useState } from 'react';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import CardActions from '@mui/material/CardActions';
import Button from '@mui/material/Button';

function About() {
  const [rawData, setRawData] = useState('');
  const [imageUrls, setImageUrls] = useState([]);

  useEffect(() => {
    // Obtén los parámetros de consulta de la URL
    const params = new URLSearchParams(window.location.search);
    const dataParam = params.get('data');

    // Si se encuentra el parámetro 'data' en la URL, establece rawData con los datos en bruto
    if (dataParam) {
      setRawData(dataParam);
    }
  }, []);

  useEffect(() => {
    // Cuando rawData cambia, intenta analizarlo como una lista de URL de imágenes
    try {
      const data = JSON.parse(rawData);
      if (Array.isArray(data)) {
        // Supongamos que data es una lista de URLs de imágenes
        setImageUrls(data);
      }
    } catch (error) {
      console.error('Error al analizar datos en bruto:', error);
    }
  }, [rawData]);

  return (
    <div>
      <h1>Estás en photos</h1>
      {imageUrls.length > 0 ? (
        <div>
          <h2>Imágenes:</h2>
          <div className="photos-container">
            {imageUrls.map((url, index) => (
              <Card
                key={index}
                sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}
              >
                <CardMedia
                  component="div"
                  sx={{
                    pt: '56.25%',
                  }}
                  image={url}
                />
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography gutterBottom variant="h5" component="h2">
                    Heading
                  </Typography>
                  <Typography>
                    This is a media card. You can use this section to describe the
                    content.
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button size="small">View</Button>
                  <Button size="small">Edit</Button>
                </CardActions>
              </Card>
            ))}
          </div>
        </div>
      ) : (
        <p>No hay imágenes para mostrar.</p>
      )}
    </div>
  );
}

export default About;
