import React, { useEffect, useState } from 'react';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import CardActions from '@mui/material/CardActions';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import Dialog from '@mui/material/Dialog';
import DialogContent from '@mui/material/DialogContent';
import PropTypes from 'prop-types';
import Navigator from './Navigator';

function Content() {
  const [rawData, setRawData] = useState('');
  const [mediaItems, setMediaItems] = useState([]);
  const [selectedMedia, setSelectedMedia] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const dataParam = params.get('data');

    if (dataParam) {
      setRawData(dataParam);
    }
  }, []);

  useEffect(() => {
    try {
      const data = JSON.parse(rawData);

      if (Array.isArray(data)) {
        setMediaItems(data);
        console.log('Mis datos', data);
      }
    } catch (error) {
      console.error('Error al analizar datos en bruto:', error);
    }
  }, [rawData]);

  const handleMediaClick = (media) => {
    setSelectedMedia(media);
    setOpenDialog(true);
  };

  const handleClick = () => {
    console.log('Click recibido desde Content');
    // Agrega la lógica adicional si es necesario
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const openVideoInNewTab = (videoUrl) => {
    window.open(videoUrl, '_blank');
  };

  console.log('Contenido de mediaItems:', mediaItems);

  return (
    <div onClick={handleClick}>
      {mediaItems.length > 0 ? (
        <div>
          <Grid container spacing={2}>
            {mediaItems.map((media, index) => (
              <Grid item xs={12} sm={6} md={4} key={index}>
                <Card
                  sx={{
                    height: '100%',
                    display: 'flex',
                    flexDirection: 'column',
                  }}
                >
                  <CardMedia
                    component="div"
                    sx={{
                      pt: '75%',
                      backgroundColor: 'blue',
                      cursor: 'pointer',
                    }}
                    image={media.baseUrl}
                    onClick={() => {
                      if (media.mimeType && media.mimeType.startsWith('image')) {
                        handleMediaClick(media);
                      } else if (media.mimeType && media.mimeType.startsWith('video')) {
                        openVideoInNewTab(media.productUrl);
                      }
                    }}
                  />

                  <CardActions sx={{ backgroundColor: '#eaeff1' }}>
                    <Button size="small">Edit</Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>

          <Dialog open={openDialog} onClose={handleCloseDialog}>
            <DialogContent>
              {selectedMedia && selectedMedia.mimeType && selectedMedia.mimeType.startsWith('image') ? (
                <img src={selectedMedia.baseUrl} alt="FullScreen" style={{ width: '100%', height: 'auto' }} />
              ) : null}
            </DialogContent>
          </Dialog>
        </div>
      ) : (
        <p>No hay medios para mostrar.</p>
      )}
      <Navigator data={mediaItems} />
    </div>
  );
}

Content.propTypes = {
  onTrashClick: PropTypes.func.isRequired,
};

export default Content;
