// Importa las librerías y componentes necesarios
import React, { useEffect, useState } from 'react';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import CardActions from '@mui/material/CardActions';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import Dialog from '@mui/material/Dialog';
import DialogContent from '@mui/material/DialogContent';

function Content() {
  const [rawData, setRawData] = useState('');
  const [imageUrls, setImageUrls] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedImageUrl, setSelectedImageUrl] = useState('');
  const [deleteImageUrl, setDeleteImageUrl] = useState('');
  const [deleteConfirmationOpen, setDeleteConfirmationOpen] = useState(false);

  useEffect(() => {
    // ... Código existente para obtener y mostrar imágenes ...
  }, [rawData]);

  // ... Funciones existentes ...

  const handleDeleteClick = (url) => {
    setDeleteImageUrl(url);
    setDeleteConfirmationOpen(true);
  };

  const handleDeleteConfirmation = (confirmed) => {
    if (confirmed) {
      // Lógica para enviar la solicitud de eliminación al backend
      fetch('http://localhost:80/delete', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url: deleteImageUrl }),
      });
      // Puedes agregar lógica adicional, como actualizar el estado local o recargar las imágenes
    }

    // Limpiar estados
    setDeleteImageUrl('');
    setDeleteConfirmationOpen(false);
  };

  return (
    <div>
      {/* ... Código existente para mostrar imágenes ... */}

      {/* Diálogo de confirmación para eliminar */}
      <Dialog open={deleteConfirmationOpen} onClose={() => setDeleteConfirmationOpen(false)}>
        <DialogContent>
          <Typography variant="body1">¿Está seguro de que desea eliminar esta imagen?</Typography>
          <Button onClick={() => handleDeleteConfirmation(true)}>Aceptar</Button>
          <Button onClick={() => handleDeleteConfirmation(false)}>Cancelar</Button>
        </DialogContent>
      </Dialog>
    </div>
  );
}

export default Content;
