// Importa useState desde React
import React, { useState } from 'react';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import useMediaQuery from '@mui/material/useMediaQuery';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Navigator from './Navigator';
import Content from './Content';
import Typography from '@mui/material/Typography';

// Crea el tema
let theme = createTheme({
  palette: {
    primary: {
      light: '#63ccff',
      main: '#009be5',
      dark: '#006db3',
    },
  },
});

export default function Paperbase() {
  // Estado para manejar el estado del menú en dispositivos móviles
  const [mobileOpen, setMobileOpen] = useState(false);
  // Verifica si el ancho de la pantalla es superior a 'sm'
  const isSmUp = useMediaQuery(theme.breakpoints.up('sm'));

  // Función para manejar la apertura y cierre del menú en dispositivos móviles
  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  // Función que será pasada como prop a Navigator para ser utilizada cuando se haga clic en la basura
  const handleTrashClick = () => {
    console.log('Click recibido desde Paperbase');
    // Agrega la lógica adicional si es necesario
  };

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ display: 'flex' }}>
        <CssBaseline />
        <Box component="nav">
          {/* Pasa la función handleTrashClick como prop al componente Navigator */}
          <Navigator
            PaperProps={{ style: { width: 256 } }}
            variant={isSmUp ? 'permanent' : 'temporary'}
            open={mobileOpen}
            onClose={handleDrawerToggle}
            onTrashClick={handleTrashClick}
          />
        </Box>
        <Box sx={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
          <Box sx={{ py: 2, px: 4, bgcolor: '#FCFCFC', color: '#030303', textAlign: 'center' }}>
            <Typography variant="h3" component="div" sx={{ fontWeight: 'bold', letterSpacing: 1.5 }}>
              Photos Google API
            </Typography>
          </Box>
          <Box component="main" sx={{ flex: 1, py: 6, px: 4, bgcolor: '#FCFCFC' }}>
            {/* Pasa la función handleTrashClick como prop al componente Content */}
            <Content onTrashClick={handleTrashClick} />
          </Box>
        </Box>
      </Box>
    </ThemeProvider>
  );
}
