import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom'; 
import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TablePagination from '@mui/material/TablePagination';
import TableRow from '@mui/material/TableRow';

const columns = [
  { id: 'productUrl', label: 'Product URL', minWidth: 10 },
  { id: 'baseUrl', label: 'Base URL', minWidth: 100 },
  { id: 'mimeType', label: 'MIME Type', minWidth: 170 },
  { id: 'nombre', label: 'Nombre', minWidth: 170 },
  { id: 'correo_electronico', label: 'Correo Electrónico', minWidth: 170 },
  { id: 'fecha_login', label: 'Fecha de Login', minWidth: 170 },
];

const DataBase = () => {
    const [data, setData] = useState(null);
    const [filteredData, setFilteredData] = useState(null);
    const [selectedDate, setSelectedDate] = useState(null);
    const location = useLocation();
    const { info } = location.state || {}; 

    useEffect(() => {
        fetchDataFromBackend();
    }, []);  

    useEffect(() => {
        filterDataByDate();
    }, [data, selectedDate]);  

    const fetchDataFromBackend = async () => {
        try {
            const response = await fetch('http://localhost:80/guardados');
            if (response.ok) {
                const jsonData = await response.json();
                setData(jsonData);
            } else {
                console.error('Error al obtener datos desde el backend:', response.status);
            }
        } catch (error) {
            console.error('Error de red:', error);
        }
    };

    const filterDataByDate = () => {
        if (!selectedDate) {
            setFilteredData(data);
        } else {
            const filtered = data.filter(item => {
                // Suponiendo que cada objeto en `data` tiene una propiedad `fecha` que contiene la fecha en formato 'YYYY-MM-DD'
                return item.fecha === selectedDate;
            });
            setFilteredData(filtered);
        }
    };

    const handleDateChange = (event) => {
        setSelectedDate(event.target.value);
    };

    return (
        <Paper sx={{ width: '100%' }}>
            <TableContainer sx={{ maxHeight: 440 }}>
                <Table stickyHeader aria-label="sticky table">
                    <TableHead>
                        <TableRow>
                            {columns.map((column) => (
                                <TableCell
                                    key={column.id}
                                    align={column.align}
                                    style={{ minWidth: column.minWidth }}
                                >
                                    {column.label}
                                </TableCell>
                            ))}
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {filteredData && filteredData.map((row, index) => {
                            return (
                                <TableRow hover role="checkbox" tabIndex={-1} key={index}>
                                    {columns.map((column) => {
                                        const value = row[column.id];
                                        return (
                                            <TableCell key={column.id} align={column.align} title={value}>
                                                {column.format && typeof value === 'number'
                                                    ? column.format(value)
                                                    : value}
                                            </TableCell>
                                        );
                                    })}
                                </TableRow>
                            );
                        })}
                    </TableBody>
                </Table>
            </TableContainer>
            <TablePagination
                rowsPerPageOptions={[10, 25, 100]}
                component="div"
                count={filteredData ? filteredData.length : 0}
                rowsPerPage={10}
                page={0}
                onPageChange={() => {}}
                onRowsPerPageChange={() => {}}
            />
        </Paper>
    );
};

export default DataBase;
