import React, { useEffect, useState } from 'react';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import CardActions from '@mui/material/CardActions';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';

function Content() {
  const [rawData, setRawData] = useState('');
  const [mediaItems, setMediaItems] = useState([]);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const dataParam = params.get('data');

    if (dataParam) {
      setRawData(dataParam);
    }
  }, []);

  useEffect(() => {
    try {
      const data = JSON.parse(rawData);

      if (Array.isArray(data)) {
        setMediaItems(data);
        console.log('Datos analizados:', data);
      }
    } catch (error) {
      console.error('Error al analizar datos en bruto:', error);
    }
  }, [rawData]);

  const openVideoInNewTab = (videoUrl) => {
    window.open(videoUrl, '_blank');
  };

  const handleImageClick = (url) => {
    window.open(url, '_blank');
  };

  return (
    <div>
      {mediaItems.length > 0 ? (
        <div>
          <Grid container spacing={2}>
            {mediaItems.map((item, index) => (
              <Grid item xs={12} sm={6} md={4} key={index}>
                <Card
                  sx={{
                    height: '100%',
                    display: 'flex',
                    flexDirection: 'column',
                  }}
                >
                  {item.mimeType && item.mimeType.startsWith('image') ? (
                    <CardMedia
                      component="div"
                      sx={{
                        pt: '75%',
                        backgroundColor: 'blue',
                        cursor: 'pointer',
                      }}
                      image={item.baseUrl}
                      onClick={() => handleImageClick(item.productUrl)}
                    />
                  ) : item.mimeType && item.mimeType.startsWith('video') ? (
                    <div>
                      <CardMedia
                        component="div"
                        sx={{
                          pt: '75%',
                          backgroundColor: 'blue',
                          cursor: 'pointer',
                        }}
                        image={item.baseUrl}
                        onClick={() => openVideoInNewTab(item.productUrl)}
                      />
                      <CardActions sx={{ backgroundColor: '#eaeff1' }}>
                        <Button size="small">Edit</Button>
                      </CardActions>
                    </div>
                  ) : (
                    <p>Tipo de medio no soportado</p>
                  )}
                </Card>
              </Grid>
            ))}
          </Grid>
        </div>
      ) : (
        <p>No hay im√°genes para mostrar.</p>
      )}
    </div>
  );
}

export default Content;
