import React, { useState } from 'react';
import Button from '@mui/material/Button';

export default function UploadPhoto() {
    const [selectedFile, setSelectedFile] = useState(null);

    const handleFileChange = (event) => {
        const folderPath = event.target.files[0].path;
        const fileName = event.target.files[0].name;
        const filePath = `${folderPath}/${fileName}`;
        setSelectedFile(filePath);
    };

    const handleUploadPhoto = async () => {
        try {
            // Subir la foto al backend
            const formData = new FormData();
            formData.append('photo', selectedFile);

            const response = await fetch('http://localhost:80/upload-photo', {
                method: 'POST',
                body: formData,
            });
            const data = await response.json();
            console.log('Foto subida:', data);
        } catch (error) {
            console.error('Error al subir la foto:', error);
        }
    };

    return (
        <div>
            <input type="file" accept="image/*" onChange={handleFileChange} />
            <Button variant="contained" onClick={handleUploadPhoto} disabled={!selectedFile}>
                Subir foto
            </Button>
        </div>
    );
}
