import React, { useEffect, useState } from 'react';

const DataBase = () => {
    const [data, setData] = useState(null);
    const location = useLocation();
    const { info } = location.state;

  // Ahora `data` contiene los datos pasados desde la página anterior
    console.log(info);

    useEffect(() => {
        // Lógica para realizar la petición al backend
        const fetchDataFromBackend = async () => {
            try {
                const response = await fetch('http://localhost:80/guardados');
                if (response.ok) {
                    const jsonData = await response.json();
                    setData(jsonData);
                } else {
                    console.error('Error al obtener datos desde el backend:', response.status);
                }
            } catch (error) {
                console.error('Error de red:', error);
            }
        };

        fetchDataFromBackend();
    }, []);  // Se ejecuta una vez cuando el componente se monta

    return (
        <div style={{ padding: '20px' }}>
         
          <div style={{ maxWidth: '800px', margin: 'auto' }}>
           
            <div style={{ display: 'flex', flexWrap: 'wrap' }}>
              {historial.map(item => (
                <div key={item.id} style={{ flex: '1 0 21%', marginBottom: '20px', marginRight: '1%' }}>
                  <div style={{ border: '1px solid #ccc', padding: '15px', borderRadius: '8px' }}>
                    <h3 style={{ marginBottom: '8px', color: '#333' }}>Texto:</h3>
                    <p style={{ fontSize: '18px', lineHeight: '1.6' }}>{item.texto}</p>
                    <h3 style={{ marginBottom: '8px', color: '#333' }}>Traducción:</h3>
                    <p style={{ fontSize: '18px', lineHeight: '1.6' }}>{item.traduccion}</p>
                    {/* Otros campos y estilos según tus necesidades */}
                  </div>
                </div>
              ))}
            </div>
          
          </div>
        </div>
      );
    };
    

export default DataBase;
