import React, { useEffect, useState } from 'react';

function Content() {
  const [rawData, setRawData] = useState('');
  const [mediaItems, setMediaItems] = useState([]);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const dataParam = params.get('data');

    if (dataParam) {
      setRawData(dataParam);
    }
  }, []);

  useEffect(() => {
    try {
      const data = JSON.parse(rawData);

      if (Array.isArray(data)) {
        setMediaItems(data);
        console.log('Datos analizados:', data);
      }
    } catch (error) {
      console.error('Error al analizar datos en bruto:', error);
    }
  }, [rawData]);

  return (
    <div style={{ display: 'flex', flexWrap: 'wrap' }}>
      {mediaItems.map((item, index) => (
        <div key={index} style={{ margin: '10px', maxWidth: '300px' }}>
          {item.mimeType && item.mimeType.startsWith('image') ? (
            <img
              src={item.baseUrl}
              alt={`Imagen ${index + 1}`}
              style={{ width: '100%', cursor: 'pointer' }}
              onClick={() => {
                // Implementa la lógica para mostrar la imagen en pantalla completa
                console.log('Haz clic en la imagen:', item.productUrl);
              }}
            />
          ) : item.mimeType && item.mimeType.startsWith('video') ? (
            <video
              width="100%"
              height="auto"
              controls
              style={{ cursor: 'pointer' }}
              onClick={() => {
                // Implementa la lógica para reproducir el video
                window.open(item.productUrl); // Abre el enlace del video en una nueva ventana
              }}
            >
              <source src={item.baseUrl} type={item.mimeType} />
              Tu navegador no soporta el tag de video.
            </video>
          ) : (
            <p>Tipo de medio no soportado</p>
          )}
        </div>
      ))}
    </div>
  );
}

export default Content;
