import React, { useEffect, useState } from 'react';

function About() {
  const [imageUrls, setImageUrls] = useState([]);

  useEffect(() => {
    // Obtén los parámetros de consulta de la URL
    const params = new URLSearchParams(window.location.search);
    const dataParam = params.get('data');

    // Si se encuentra el parámetro 'data' en la URL, analízalo y obtén las URL de las imágenes
    if (dataParam) {
      try {
        const jsonData = JSON.parse(dataParam);
        if (Array.isArray(jsonData)) {
          // Supongamos que jsonData es un array de objetos con una propiedad 'productUrl'
          const urls = jsonData.map(item => item.productUrl);
          setImageUrls(urls);
        }
      } catch (error) {
        console.error('Error al analizar JSON:', error);
      }
    }
  }, []);

  return (
    <div>
      <h1>Estás en photos</h1>
      {imageUrls.length > 0 ? (
        <div>
          <h2>Imágenes:</h2>
          <div className="photos-container">
            {imageUrls.map((url, index) => (
              <div key={index} className="photo-container">
                <img src="https://lh3.googleusercontent.com/lr/AAJ1LKdqkJz-DuMRJyY2mWON04IYiLUvksJjaCrPaKZNy-O09ETuyZiiyJi9z3IpXmiLEJPkk8jBMsOmHKVhnVxyN9flVEwR2XMRfq73Xqqeg82NXQR_XFwZFSiKE8wierrRNsCv9DqIsgyygExZe54xUOFPk8xn5kPY4K9Ferj_tctc1vw1iidZHQ2PL3pkBROBQ3HvglX0qXxIaj5fWuz0pg25RSoZNoTxd0B8VC7q5_pHT8JgbBxZRBa85rTaTDp-xx_RHRJbTulOEmyjellNxw5QPOgF4uM_8U3Zos5W2GhnbUmBu4RQYA6ZhMeyBj_IBf041h8-_sgWjNZwqfiMKL09AWt0koBXfNbtVOr9Y68OKS1DHcjA62ubjpOieK1cXf0eMCGfO95POJlsPHzxSmkncPKQFrqEH4_FMH2KRrHVZfYgr23yXUXyt-A-085OYSWBtac7LMnLgHM3sR0PLyFfuOTsI2MCanKF1iDdNRJLnKHSvQJ5ms64Mk8VGw_i5OFYj9ucNX0UHh9N-tIAjaylY-L7zLyXAapgrHwFOX43dVUOxSCOBc5WMJJhsUb3TAd8ETQbcB6IoXoMAvPVm-veLR0ffmSIpzbqOO3vipmPzYI5QVJ82BFecNuB7Itul-wgs1DWOi46sAvvk771Lzl6RGz-LX4K4N-yQzi2X9PXrLPExRxTdxIx53_jHmOmOHmcbaJsivP3lJ67wDoTBEr0do-pnVwK4U8njhLmmonJ3RNDWs-dGxWtxDYuENxWyazZgBZQMPrWwQLQ0zhD6uDCv49hy09XY-msIaSJdKdstoZEuhtVf4kn9aPdorNcnn25UCkOabyGH8BTMJKREnpIDuNi_BjzMLamwEjXgSE-J68R0qa6OrBoza6hPUJSTERD3qpBaRmHTVqTOajGGpw8RkJBD9XPU0DZbYaAoCN9KxkHpUDSATMTS-avr_uDzcfArTz6bT3VQ5f858Opfomi_mlp5MVbi56aE6YL11Cc90gt" alt="Mi imagen" />
              </div>
            ))}
          </div>
        </div>
