import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom'; 

const DataBase = () => {
    const [data, setData] = useState(null);
    const [filteredData, setFilteredData] = useState(null);
    const [selectedDate, setSelectedDate] = useState(null);
    const location = useLocation();
    const { info } = location.state || {}; 

    useEffect(() => {
        fetchDataFromBackend();
    }, []);  

    useEffect(() => {
        filterDataByDate();
    }, [data, selectedDate]);  

    const fetchDataFromBackend = async () => {
        try {
            const response = await fetch('http://localhost:80/guardados');
            if (response.ok) {
                const jsonData = await response.json();
                setData(jsonData);
            } else {
                console.error('Error al obtener datos desde el backend:', response.status);
            }
        } catch (error) {
            console.error('Error de red:', error);
        }
    };

    const filterDataByDate = () => {
        if (!selectedDate) {
            setFilteredData(data);
        } else {
            const filtered = data.filter(item => {
                // Suponiendo que cada objeto en `data` tiene una propiedad `fecha` que contiene la fecha en formato 'YYYY-MM-DD'
                return item.fecha === selectedDate;
            });
            setFilteredData(filtered);
        }
    };

    const handleDateChange = (event) => {
        setSelectedDate(event.target.value);
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1 style={{ marginBottom: '20px' }}>Base de Datos</h1>
            <div style={{ marginBottom: '20px' }}>
                <label htmlFor="dateFilter">Filtrar por fecha:</label>
                <input type="date" id="dateFilter" value={selectedDate} onChange={handleDateChange} />
            </div>
            <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                {filteredData && filteredData.map((item, index) => (
                    <div key={index} style={{ flex: '1 0 auto', marginBottom: '20px', marginRight: '20px', border: '1px solid #ccc', borderRadius: '8px', padding: '15px' }}>
                        {Object.keys(item).map((key, index) => (
                            <div key={index} style={{ display: 'flex', flexDirection: 'column', marginBottom: '10px' }}>
                                <h3 style={{ marginBottom: '8px', color: '#333' }}>{key}:</h3>
                                <p>{item[key]}</p>
                            </div>
                        ))}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default DataBase;
