import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom'; // Importa useLocation desde react-router-dom

const DataBase = () => {
    const [data, setData] = useState(null);
    const location = useLocation();
    const { info } = location.state || {}; // Usando un operador condicional para asignar un objeto vacío si location.state es null

    // Ahora `data` contiene los datos pasados desde la página anterior
    console.log(info);

    useEffect(() => {
        // Lógica para realizar la petición al backend
        const fetchDataFromBackend = async () => {
            try {
                const response = await fetch('http://localhost:80/guardados');
                if (response.ok) {
                    const jsonData = await response.json();
                    setData(jsonData);
                } else {
                    console.error('Error al obtener datos desde el backend:', response.status);
                }
            } catch (error) {
                console.error('Error de red:', error);
            }
        };

        fetchDataFromBackend();
    }, []);  // Se ejecuta una vez cuando el componente se monta

    return (
        <div style={{ padding: '20px' }}>
            <h1 style={{ marginBottom: '20px' }}>Base de Datos</h1>
            <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                {data && data.map(item => (
                    <div key={item.id} style={{ flex: '1 0 21%', marginBottom: '20px', marginRight: '1%' }}>
                        <div style={{ border: '1px solid #ccc', padding: '15px', borderRadius: '8px' }}>
                            <h3 style={{ marginBottom: '8px', color: '#333' }}>Estado:</h3>
                            <p>{item.estado}</p>
                            <h3 style={{ marginBottom: '8px', color: '#333' }}>ID:</h3>
                            <p>{item.id}</p>
                            <h3 style={{ marginBottom: '8px', color: '#333' }}>Link:</h3>
                            <p><a href={item.link} target="_blank" rel="noopener noreferrer" style={{ color: '#007bff' }}>Enlace</a></p>
                            <h3 style={{ marginBottom: '8px', color: '#333' }}>Tipo:</h3>
                            <p>{item.tipo}</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default DataBase;
