import React, { useState } from 'react';
import styled from '@mui/system/styled';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import MenuIcon from '@mui/icons-material/Menu';
import EditIcon from '@mui/icons-material/Edit';
import Drawer from '@mui/material/Drawer';
import Box from '@mui/material/Box';
import ListItem from '@mui/material/ListItem';
import Button from '@mui/material/Button';
import Avatar from '@mui/material/Avatar';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';

// Constantes para estilos repetidos
const circularIconButtonStyles = {
  borderRadius: '50%',
  backgroundColor: '#F9AA33',
  '&:hover': {
    backgroundColor: '#F9AA33',
  },
  width: '40px',
  height: '40px',
  '@media only screen and (max-width: 600px)': {
    width: '32px',
    height: '32px',
  },
};

const homeIconButtonStyles = {
  ...circularIconButtonStyles,
  marginLeft: '-8px',
};

const StyledAppBar = styled(AppBar)({
  top: 'auto',
  bottom: 0,
  background: '#344955',
  boxShadow: 'none',
  borderTop: '1px solid #FCFCFC',
  position: 'fixed',
  width: '100%',
  '&::before': {
    content: '""',
    position: 'absolute',
    top: '-40%',
    left: '50%',
    transform: 'translateX(-50%)',
    width: '5%',
    height: '100%',
    background: '#FCFCFC',
    borderRadius: '50%',
  },
});

const CenteredBox = styled('div')({
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  width: '100%',
  padding: '0 16px',
  '@media only screen and (max-width: 600px)': {
    padding: '0 8px',
  },
});

const CircularIconButton = styled(IconButton)(circularIconButtonStyles);

const HomeIconButton = styled(CircularIconButton)(homeIconButtonStyles);

const categories = [
  {
    children: [
      { id: 'Bandeja de entrada', icon: <Inbox /> },
      { id: 'Base de datos', icon: <DnsRoundedIcon /> },
      { id: 'Enviado', icon: <Send /> },
      { id: 'Basura', icon: <Delete /> },
      { id: 'Spam', icon: <Info /> },
      { id: 'Borrador', icon: <Drafts /> },
    ],
  },
  {
    id: 'Carpetas',
    children: [
      { id: 'Ingreso', icon: <Folder /> },
      { id: 'Vacaciones', icon: <Folder /> },
      { id: 'Impuesto', icon: <Folder /> },
    ],
  },
];

export default function BottomAppBarWithNavigation({ data }) {
  console.log('Contenido de la prop data:', data);
  let correo_electronico, nombre, foto_perfil_url;
  if (data && data.length > 0) {
    // Extraer datos del primer elemento (índice 0)
    ({ correo_electronico, nombre, foto_perfil_url } = data[0]);
  }

  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [openDialog, setOpenDialog] = useState(false);

  const handleMenuToggle = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleProfileClick = () => {
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  return (
    <div>
      <StyledAppBar>
        <Toolbar>
          <CenteredBox>
            <IconButton size="large" edge="start" color="#344955 " aria-label="open drawer" onClick={handleMenuToggle}>
              <MenuIcon />
            </IconButton>
            <HomeIconButton size="large" color="#fff" aria-label="edit" style={{ marginLeft: '0px', marginTop: '-50px' }}>
              <EditIcon />
            </HomeIconButton>
            <IconButton size="large" edge="end" color="#fff" aria-label="account">
              <AccountCircleIcon />
            </IconButton>
          </CenteredBox>
        </Toolbar>
      </StyledAppBar>

      <Drawer variant="temporary" open={isMenuOpen} onClose={handleMenuToggle}>
        <Box disablePadding sx={{ bgcolor: ' #344955 ', height: '100%' }}>
          <ListItem sx={{ py: 2, px: 3, fontSize: 22, color: '#fff', justifyContent: 'flex-end' }}>
            <Box>
              <Avatar alt={nombre} src={foto_perfil_url} onClick={handleProfileClick} />
            </Box>

            <Dialog open={openDialog} onClose={handleCloseDialog}>
              <DialogTitle>{nombre}</DialogTitle>
              <DialogContent>
                <Box>
                  <Avatar alt={nombre} src={foto_perfil_url} />
                  <div>{`Correo Electrónico: ${correo_electronico}`}</div>
                </Box>
                <Box>
                  {/* Agrega más detalles según tus necesidades */}
                  <div>{`Otro Dato: ...`}</div>
                </Box>
              </DialogContent>
            </Dialog>

            <Box>
              <IconButton color="inherit">
                <SettingsIcon />
              </IconButton>
            </Box>
          </ListItem>
          <ListItem sx={{ py: 2, px: 3 }}>
            <Button
              variant="outlined"
              sx={{
                borderRadius: '25px',
                color: '#344955 ',
                bgcolor: '#FFA500',
                typography: {
                  fontFamily: 'Libre Franklin',
                  fontWeight: 'bold',
                  fontSize: '15px',
                  letterSpacing: '0.15px',
                },
              }}
            >
              <EditIcon />
              COMPOSE
            </Button>
          </ListItem>
          {categories.map(({ id, children }) => (
            <Box key={id} sx={{ bgcolor: ' #344955 ' }}>
              <ListItem key={id} sx={{ py: 2, px: 3 }}>
                <div>{id}</div>
              </ListItem>
              {children.map(({ id: childId, icon, active }) => (
                <ListItem key={childId} disablePadding>
                  <div>{childId}</div>
                </ListItem>
              ))}
            </Box>
          ))}
        </Box>
      </Drawer>

      <div style={{ marginLeft: isMenuOpen ? '240px' : '0', padding: '16px', marginTop: '64px', transition: 'margin-left 0.3s ease-in-out' }}>
        {/* Aquí puedes agregar el contenido principal de tu aplicación */}
      </div>
    </div>
  );
}
